import axios from "axios";

class ProvaApi {
    axiosClient = axios.create();

    makeRequest = async (url) => {
        try { 
            const fullUrl = `https://cors-anywhere.herokuapp.com/https://www.fotmob.com/${url}`;
            const response = await this.axiosClient({
                method: "GET",
                url: fullUrl,
            });    
            return response;
        } catch (error) {
            console.error(error);
            throw error;
        }
    };

    makeSearch = async (url) => {
        try { 
            const fullUrl = `https://cors-anywhere.herokuapp.com/https://apigw.fotmob.com/${url}`;
            const response = await this.axiosClient({
                method: "GET",
                url: fullUrl,
            });    
            return response;
        } catch (error) {
            console.error(error);
            throw error;
        }
    };
}

export default ProvaApi;
