import React, { useEffect, useState } from "react";
import StandingLine from "./StandingLine";
import ApiManager from "../../api/ApiManager"; 
import ProvaApi from "../../api/ProvaApi";

function Standings(props) {
    // const api = new ApiManager();
    const apiProva = new ProvaApi();
    const [standingData, setStandingData] = useState([]);
    const [leagueData, setLeagueData] = useState([]);

    // useEffect(() => {
    //     const fetchData = async () => {
    //         try {
    //             // const responseStanding = await api.makeRequest(`standings?league=${props.leagueId}&season=2023`);
    //             // setStandingData(responseStanding.response[0].league.standings[0]);
    //             // setLeagueData(responseStanding.response[0].league);
    //             const fakeData = Array.from({ length: 20 }, (_, index) => ({
    //                 team: { id: 85, logo: `https://media.api-sports.io/football/teams/${index + 33}.png`, name: `Team ${index + 1}` },
    //                 all: { win: index+2, draw: index, lose: index+5, played: index+10 },
    //                 points: index+4,
    //                 rank: index + 1,
    //                 index: index,
    //                 goalsDiff: index+20
    //             }));

    //             // setStandingData(fakeData);
    //             // setLeagueData({ name: props.leagueName, icon: props.leagueIcon });
    //         } catch (error) {
    //             console.log(error);
    //         }
    //     };

    //     fetchData(); 

    // }, []);

    useEffect(() => {
        const fetchProva = async () => {
            try {
                const responseProva = await apiProva.makeRequest(`api/tltable?leagueId=${props.id}`);
                console.log(responseProva.data[0].data.table.all)
                setStandingData(responseProva.data[0].data.table.all);
                setLeagueData(responseProva.data[0].data);

            } catch (error) {
                console.log(error);
            }
        };

        fetchProva(); 

    }, []);

    return (
        <>
            <div className="flex flex-col gap-5 p-4 rounded-lg bg-gray-100 w-auto">
                <div className="fixed flex justify-between relative">
                    <div className="flex gap-5 items-center basis-6/12">
                    <img className="h-10 w-10 object-contain" src={leagueData.logo || "https://media.api-sports.io/football/leagues/135.png"} alt="leagueIcon" />
                        <div className="text-xl font-medium">{leagueData.leagueName || "sdasfas A"}</div> 
                    </div>
                    <div className="flex items-center basis-5/12 text-end">
                        <div className="text-[#6d6a7f] basis-4/12 md:block md:basis-2/12">P</div>
                        <div className="text-[#6d6a7f] basis-4/12 hidden md:block md:basis-2/12">W</div>
                        <div className="text-[#6d6a7f] basis-4/12 hidden md:block md:basis-2/12">D</div>
                        <div className="text-[#6d6a7f] basis-4/12 hidden md:block md:basis-2/12">L</div>
                        <div className="text-[#6d6a7f] basis-4/12 md:block md:basis-2/12">GD</div>
                        <div className="font-medium basis-4/12 md:block md:basis-2/12">PT</div>
                    </div>
                </div>
                <div className="max-h-96 overflow-y-auto flex flex-col gap-8">
                    {standingData.map((team, index) => (
                        <StandingLine
                            key={index}
                            index={team.idx || props.idx}
                            teamLogo="https://media.api-sports.io/football/teams/42.png"
                            teamName={team.name}
                            teamWins={team.wins}
                            teamDraws={team.draws}
                            teamLose={team.losses}
                            teamPoints={team.pts}
                            teamPlayed={team.played}
                            number={team.idx}
                            teamId={team.id}
                            // teamLeagueId={leagueData.id}
                            teamDR={team.goalConDiff}
                            // teamState={team.description}
                        />
                    ))}
                </div>
                <div className="flex flex-col md:flex-row gap-3">
                    <div className="flex gap-2 items-center">
                        <div className="w-2 h-2 rounded-full border-4 border-green-500"></div>
                        <div className="text-sm text-[#6d6a7f]">Champions League</div>
                    </div>
                    <div className="flex gap-2 items-center">
                        <div className="w-2 h-2 rounded-full border-4 border-yellow-500"></div>
                        <div className="text-sm text-[#6d6a7f]">Europa League</div>
                    </div>
                    <div className="flex gap-2 items-center">
                        <div className="w-2 h-2 rounded-full border-4 border-red-500"></div>
                        <div className="text-sm text-[#6d6a7f]">Relegation</div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Standings;
