import axios from "axios";

class ApiManager {
    baseHost = process.env.REACT_APP_BASE_URL;
    apikey = process.env.REACT_APP_API_KEY

    async makeRequest(endpoint) {
        const axios = require('axios');

        const headers = {
        "X-Auth-Token": "3771bc934dd74edb83d59ca93b6fb551",
        "Host": "3771bc934dd74edb83d59ca93b6fb551"
        };

        const requestOptions = {
        method: 'GET',
        url: "http://api.football-data.org/v4/competitions/PL/standings",
        headers: headers,
        };
        const axiosOptions = {
            method: "GET",
            url: `${this.baseHost}${endpoint}`,
            headers: {
                "X-Auth-Token": this.apiKey,
                "Host": this.apiKey,
            },
        };    
        try {
            const response = await axios(axiosOptions);
            return response;
        } catch (error) {
            console.log("Error:", error);
            throw error;
        }
    }
}

export default ApiManager;
