import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import ProvaApi from "../api/ProvaApi";
import Layout from "../components/Layout/Layout";
import LeagueDetailsTop from "../components/League/LeagueDetailsTop";

const Leagues = () => {
	const {id} = useParams();
    const apiProva = new ProvaApi();
    const [leagueData, setLeagueData] = useState([]);

	useEffect(() => {
        const fetchLeague = async () => {
            try {
                const responseLeagueData = await apiProva.makeRequest(`api/leagues?id=${id}&ccode3=ITA`);
                setLeagueData(responseLeagueData.data);  
            } catch (error) {
                console.log(error);
            }
        };

        fetchLeague(); 

    }, [id]);

	const [totwData, setTotwData] = useState([]);

	useEffect(() => {
        const fetchTotw = async () => {
            try {
                const responseTotwData = await apiProva.makeRequest(`api/team-of-the-week/team?leagueId=${id}&roundId=${leagueData?.matches?.firstUnplayedMatch?.firstRoundWithUnplayedMatch-1}&season=2023%2F2024`);
                setTotwData(responseTotwData);  
                console.log(totwData)
            } catch (error) {
                console.log(error);
            }
        };

        fetchTotw(); 

    }, [id]);

	return (
		<>
			<Layout />
			<div className="text-black flex flex-col p-4 gap-3">
				<LeagueDetailsTop totwData={totwData} data={leagueData} leagueId={id}/>
				{/* <div className="flex flex-col md:flex-row gap-3">	
					<div className="flex basis-6/12">
					</div>
					<div className="flex basis-6/12">
						<Standings id={id} />
					</div>
				</div> */}
			</div>
		</>
	);
};

export default Leagues;
