import ApiManager from "../../api/ApiManager";
import React, { useEffect, useState } from "react";

function TransferCard(props) {
    const api = new ApiManager();
    const [transferData, setTransferData] = useState([]);

    useEffect(() => {
        const fetchTransfer = async () => {
            try {
                const response = await api.makeRequest(`transfers?player=276`);
                setTransferData(response.response[0]);
            } catch (error) {
                console.log(error);
            }
        };

        fetchTransfer();
    }, []); 

    useEffect(() => {
        console.log(transferData);
    }, [transferData]); 


    return (
        <div className="p-4 rounded-lg w-full bg-gray-100">
            <img className="text-base font-medium" src={transferData.player.name || "Neymar"} alt="teamLogo" />
            <div className="flex justify-between">
                <div className="flex flex-col text-center gap-2 relative">
                    <div className="text-xs font-medium">{transferData.transfers[0].teams.in.name|| "Neymar"}</div>
                    <span className="top-0 left-7 absolute w-3.5 h-3.5 bg-green-400 border-2 border-white rounded-full"></span>
                </div>
                <div className="flex gap-2 items-center">
                    <div className="text-lg font-medium">1</div>
                </div>
                <div className="flex flex-col text-center gap-2">
                    <img className="h-10 object-fit" src="https://media.api-sports.io/football/teams/42.png" alt="teamLogo" />
                    <div className="text-xs font-medium">ARS</div>
                </div>
            </div>
            <div className="flex justify-center">
            {transferData.transfers[0].type === "Free" ? (
                <span className="bg-green-100 text-green-800 text-xs font-medium px-2.5 py-0.5 rounded border border-green-400">Free</span>
                ) : null}
            </div>
        </div>
    )
}

export default TransferCard
