import React from "react";
import {
	AreaChart,
	Area,
	XAxis,
	YAxis,
	ReferenceLine,
	Tooltip
  } from "recharts";

function Momentum(props) {
	const [chartDimensions, setChartDimensions] = useState({ width: 800, height: 200 });
  const chartContainerRef = useRef(null);
	const data = props.data?.content?.momentum?.main?.data;
	console.log(data);

	let minutes = [];
	let values = [];

	data?.map((moment, index) => {
		minutes.push(moment.minute);
		values.push(moment.value);
	});

	console.log(document.getElementById('foo').getBoundingClientRect())

	const gradientOffset = () => {
		const dataMax = Math.max(data)
		const dataMin = Math.min(data)

		if (dataMax <= 0) {
		  return 0;
		}
		if (dataMin >= 0) {
		  return 1;
		}

		return dataMax / (dataMax - dataMin);
	};

	const off = gradientOffset();

	return (
		<div className="w-full flex flex-col rounded-lg py-4 bg-white brd gap-4">
		<div className="text-center font-medium text-base px-4">Match Momentum</div>
		<div className="brd05"></div>
		<div className="h-32 md:h-64 relative" id="foo">
			<AreaChart
			width={800}
			height={200}
			data={data}
			margin={{
				top: 20,
				right: 30,
				left: 30,
				bottom: 20,
			}}
			>
			<XAxis
				stroke="#e8e8e8"
				strokeWidth={4}
				strokeDasharray="0.0001, 9.85704"
				strokeLinecap="round"
				tick={false}
			/>
			<YAxis />
			<ReferenceLine x={45}
				stroke="#e8e8e8"
				strokeWidth={4}
				strokeDasharray="0.0001, 9.85704"
				strokeLinecap="round"
				label={<><div className="w-fit px-2 py-0.5 bg-white rounded-full font-bold font-sm md:font-base">HT</div></>}
			/>
			<ReferenceLine y={0}
				stroke="#e8e8e8"
				strokeWidth={4}
				strokeDasharray="0.0001, 9.85704"
				strokeLinecap="round"
				label={<><div className="w-fit px-2 py-0.5 bg-white rounded-full font-bold font-sm md:font-base">HT</div></>}
			/>
			<defs>
				<linearGradient id="splitColor" x1="0" y1="0" x2="0" y2="1">
				<stop offset={off} stopColor={props.colorData?.home} stopOpacity={1} />
				<stop offset={off} stopColor={props.colorData?.away} stopOpacity={1} />
				</linearGradient>
			</defs>
			<Area type="monotone" dataKey="uv" stroke="#000" fill="url(#splitColor)" />
			</AreaChart>
		</div>
		</div>
	);
}

export default Momentum;
