import { useEffect } from "react";
import { useState } from "react";
import { Link } from "react-router-dom"
import SearchBar from "./SearchBar"

function TopBar() {
    const [favoriteData, setFavoriteData] = useState([])

    useEffect(() => {
        const storedFavorites = localStorage.getItem("favorites");
        if (storedFavorites) {
            const fav = JSON.parse(storedFavorites);
            setFavoriteData(fav)
            console.log(favoriteData);
        } else {
            console.log("Nessun dato trovato nel localStorage");
        }
    }, [])

    return (
        <>
            <div className="sticky top-0 left-0 w-full z-50">
                <div className="px-6 py-4 bg-white brd flex justify-between items-center rounded-b-lg">
                    <div className="flex gap-10 items-center">
                        <Link to="/">
                            <img alt="balunlogo" src="https://i.ibb.co/HV361c4/Balu-n.png" className="h-6"></img>
                        </Link>
                        <div className="hidden md:block">
                            <SearchBar />
                        </div>
                    </div>
                    <div className="flex gap-4 md:gap-8 items-center">
                        <Link to="/league-list/">
                            <div className="flex gap-3 items-center">
                                <img src="https://img.icons8.com/?size=512&id=Qqh4f0AP23uP&format=png" alt="leaguesIcon" className="hidden md:block h-4" />
                                <div className="text-lg">Leagues</div>
                            </div>
                        </Link>
                        <Link to="/">
                            <div className="flex gap-3 items-center">
                                <img src="https://i.ibb.co/09rTv67/SVG-to-PNG-Converter.png" alt="leagueIcon" className="hidden md:block h-4" />
                                <div className="text-lg ">Transfers</div>
                            </div>
                        </Link>
                        <Link to={"/team/" + (favoriteData?.[0]?.teamData?.details?.id)}>
                            <div className="h-4">
                                <img alt="logo" className="h-8" src={favoriteData?.[0]?.teamLogo} />
                            </div>
                        </Link>
                        <div className="block md:hidden">
                            <SearchBar />
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default TopBar
