import { useParams } from "react-router-dom"
import ApiManager from "../api/ApiManager";
import React, { useEffect, useState } from "react";
import SideBar from "../components/SideBar/Sidebar";
import TeamProfile from "../components/team/TeamProfile";
import LatestFuture from "../components/team/LatestFuture";
import PlayerCard from "../components/team/PlayerCard";
import Standings from "../components/standings/Standings";

const TeamDetail = (props) => {
    const {id} = useParams();
    const api = new ApiManager();
    const [teamData, setTeamData] = useState([]);
    const [venueData, setVenueData] = useState([]);

    useEffect(() => {
        const fetchTeamDetail = async () => {
            try {
                const response = await api.makeRequest(`teams?id=${id}`);
                setTeamData(response.response[0].team);
                setVenueData(response.response[0].venue);
            } catch (error) {
                console.log(error);
            }
        };

        fetchTeamDetail();
    }, []); 

	return (
		<>
            <SideBar />
			<div className="text-black flex flex-col p-4 sm:ml-64 gap-5">
                <TeamProfile teamName={teamData.name || "Paris Saint Germain"} teamNation={teamData.country || "France"} teamLogo={teamData.logo || "https://media.api-sports.io/football/teams/85.png"} teamVenueName={venueData.name || "Parc de Princes"} teamVenueCapacity={venueData.capac || "Parc de Princes"}/>
                <LatestFuture />
                <div className="flex flex-col gap-2">
                    <div className="flex justify-between">
                        <div className="text-sm text-[#6d6a7f]">First Team</div>
                        <div className="text-sm text-[#6d6a7f]">2023-24</div>
                    </div>
                    <div className="overflow-x-auto ">
                        <PlayerCard />
                    </div>
                </div>
                <Standings leagueId={props.teamLeagueId}/>
            </div>	
		</>
	);
};

export default TeamDetail;
