import { useParams } from "react-router-dom"
import ApiManager from "../api/ApiManager";
import React, { useEffect, useState } from "react";

const TeamDetail = () => {
    const {id} = useParams();
    const api = new ApiManager();
    const [teamData, setTeamData] = useState([]);

    useEffect(() => {
        const fetchStandings = async () => {
            try {
                const response = await api.makeRequest(`teams?id=${id}`);
                setTeamData(response.response[0]);
            } catch (error) {
                console.log(error);
            }
        };

        fetchStandings();
    }, []); 


	return (
		<>
			<div className="main flex">
				<div className="text-white flex flex-col w-full">
                    <div className="absolute min-w-full h-50">
                        <img src={teamData.venue.image} alt="teamVenue" className="object-cover" />
                    </div>
                    <div className="flex justify-center p-4">
                        <img src={teamData.team.logo} className="h-30 w-30 object-fit" alt="teamLogo"></img>
                    </div>
				</div>
			</div>
		</>
	);
};

export default TeamDetail;
