import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import ProvaApi from "../../api/ProvaApi";

function SearchBar() {
    const apiProva = new ProvaApi();
    const [searchData, setSearchData] = useState([]);
    const [searchValue, setSearchValue] = useState("");

    const handleSearchInputChange = (event) => {
        setSearchValue(event.target.value);
    };

    useEffect(() => {
        const fetchData = async () => {
            try {
                const responseFromSearch = await apiProva.makeSearch(`searchapi/suggest?term=${searchValue}&lang=en`);
                setSearchData(responseFromSearch);
                console.log(searchData);
            } catch (error) {
                console.log(error);
            }
        };

        fetchData();
    }, [searchValue]);

    const handleError = (event) => {
        event.target.src = "https://i.ibb.co/q9SWXK2/player-placeholder.png";
    };

    return (
        <div className="flex flex-col gap-8">
            <form className="w-80">
                <div className="relative">
                    <div className="absolute inset-y-0 start-0 flex items-center ps-3 pointer-events-none">
                        <svg xmlns="http://www.w3.org/2000/svg" height="16" width="16" viewBox="0 0 512 512">
                            <path d="M416 208c0 45.9-14.9 88.3-40 122.7L502.6 457.4c12.5 12.5 12.5 32.8 0 45.3s-32.8 12.5-45.3 0L330.7 376c-34.4 25.2-76.8 40-122.7 40C93.1 416 0 322.9 0 208S93.1 0 208 0S416 93.1 416 208zM208 352a144 144 0 1 0 0-288 144 144 0 1 0 0 288z"/>
                        </svg>
                    </div>
                    <input
                        id="searchInput"
                        className="block w-full p-2 ps-10 text-sm text-gray-900 border border-gray-300 rounded-full bg-gray-50 focus:outline-none"
                        placeholder="Search for teams, leagues and fixtures"
                        value={searchValue}
                        onChange={handleSearchInputChange}
                        required
                    />
                </div>
            </form>
            {searchValue !== "" && 
                <div className="mt-14 rounded-lg absolute w-80 bg-gray-100 p-3 drop-shadow-xl flex flex-col gap-6">
                    {/* {searchData?.map((searchResult, index) => (
                        ""
                    ))} */}
                    {searchData.data.leagueSuggest &&
                    <div className="flex flex-col gap-6">
                        <div className="text-lg font-medium">Leagues</div>
                    </div>
                    }
                    {searchData.data.matchSuggest &&
                    <div className="flex flex-col gap-6">
                        <div className="text-lg font-medium">Matches</div>
                        {searchData?.data?.matchSuggest?.[0]?.options.map((match, index) => {
                            return (
                                <Link to={"/match-details/" + match?.payload.id}>
                                    <div key={index} className="flex gap-3 items-center justify-between">
                                        <img alt="playerPhoto" className="h-7" src={`https://images.fotmob.com/image_resources/logo/teamlogo/${match?.payload?.homeTeamId}.png`} />
                                        <div className="text-sm text-nowrap truncate">{match?.payload?.homeName}</div>
                                        <div className="text-sm text-nowrap">-</div>
                                        <div className="text-sm text-nowrap truncate">{match?.payload?.awayName}</div>
                                        <img alt="playerPhoto" className="h-7" src={`https://images.fotmob.com/image_resources/logo/teamlogo/${match?.payload?.awayTeamId}.png`} />
                                    </div>
                                </Link>
                            );
                        })}
                    </div>
                    }
                    {searchData.data.squadMemberSuggest &&
                        <div className="flex flex-col gap-6">
                            <div className="text-lg font-medium">Players and Managers</div>
                            {searchData?.data?.squadMemberSuggest?.[0]?.options.map((squadMember, index) => {
                                const parts = squadMember?.text?.split('|');
                                const playerName = parts[0];
                                const playerId = parts[1]; 
                                return (
                                    <Link to={"/player/" + playerId}>
                                        <div key={index} className="flex gap-3 items-center">
                                            <div className="overflow-hidden h-7 w-7 rounded-full bg-white ">
                                                <img alt="playerPhoto" className="h-7" onError={handleError} src={`https://images.fotmob.com/image_resources/playerimages/${playerId}.png`} />
                                            </div>
                                            <div className="text-sm">{playerName}</div>
                                        </div>
                                    </Link>
                                );
                            })}
                        </div>
                    }
                    {searchData.data.teamSuggest &&
                    <div className="flex flex-col gap-6">
                        <div className="text-lg font-medium">Teams</div>
                        {searchData?.data?.teamSuggest?.[0]?.options.map((team, index) => {
                            const parts = team?.text.split('|');
                            const teamName = parts[0];
                            const teamId = parts[1]; 
                            return (
                                <Link to={"/team/" + teamId}>
                                    <div key={index} className="flex gap-3 items-center">
                                        <img alt="teamLogo" className="h-7" src={`https://images.fotmob.com/image_resources/logo/teamlogo/${teamId}.png`} />
                                        <div className="text-sm">{teamName}</div>
                                    </div>
                                </Link>
                            );
                        })}
                    </div>
                    }
                </div>
            }
        </div>
    );
}

export default SearchBar;
