import axios from 'axios';

class ApiManager {
    apiKey = process.env.REACT_APP_API_KEY;
    baseHost = process.env.REACT_APP_BASE_URL;

    async makeRequest(endpoint, method = 'GET', params = {}) {
        const axiosOptions = {
            method: method,
            url: `https://${this.baseHost}/${endpoint}`,
            headers: {
                'x-rapidapi-key': this.apiKey,
                'x-rapidapi-host': this.baseHost,
            },
            params: params,
            redirect: 'follow',
        };

        try {
            const response = await axios(axiosOptions);
            return response.data;
        } catch (error) {
            console.log('Error:', error);
            throw error;
        }
    }

  // Example method to get leagues
  async getLeagues() {
    try {
      return await this.makeRequest('leagues');
    } catch (error) {
      console.log('Error fetching leagues:', error);
      throw error;
    }
  }

  // Add more methods for other endpoints as needed
}

export default ApiManager;
