import React, { PureComponent } from 'react';
import { AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

export default class Momentum extends PureComponent {
	static demoUrl = 'https://codesandbox.io/s/area-chart-filled-by-sign-0h7rt';

	render() {
		const data = this.props.data?.content?.momentum?.main?.data;
		console.log(data);

		let minutes = [];
		let values = [];

		data?.map((moment, index) => {
			minutes.push(moment.minute);
			values.push(moment.value);
		});

		const gradientOffset = () => {
			const dataMax = Math.max(...values);
			const dataMin = Math.min(...values);

			if (dataMax <= 0) {
				return 0;
			}
			if (dataMin >= 0) {
				return 1;
			}

			return dataMax / (dataMax - dataMin);
		};

		const off = gradientOffset();

		return (
			<AreaChart
				width={500}
				height={400}
				data={values}
				margin={{
					top: 10,
					right: 30,
					left: 0,
					bottom: 0,
				}}
				>
				{/* <CartesianGrid strokeDasharray="3 3" /> */}
				{/* <XAxis dataKey="name" /> */}
				<YAxis />
				<Tooltip />
				<defs>
					<linearGradient id="splitColor" x1="0" y1="0" x2="0" y2="1">
					<stop offset={off} stopColor="green" stopOpacity={1} />
					<stop offset={off} stopColor="red" stopOpacity={1} />
					</linearGradient>
				</defs>
				<Area type="monotone" dataKey="uv" stroke="#000" fill="url(#splitColor)" />
			</AreaChart>
		);
	}
}
