import { useEffect, useState } from "react";
import ProvaApi from "../../api/ProvaApi";

function MatchLive(props) {
  const data = props.data;
  const apiProva = new ProvaApi();
  const [liveData, setLiveData] = useState([]);

  useEffect(() => {
    const fetchLive = async () => {
      try {
        const timestamp = new Date().getTime();
        const responseLiveData = await apiProva.makeRequest(`api/ltc?ltcUrl=data.fotmob.com%2Fwebcl%2Fltc%2Fgsm%2F4169732_en_gen.json.gz&teams=%5B%22Iraq%22%2C%22Japan%22%5D&_=${timestamp}`);
        setLiveData(responseLiveData.data);
      } catch (error) {
        console.log(error);
      }
    };

    fetchLive();

  }, [props]);

    const handleError = (event) => {
        event.target.src = "https://i.ibb.co/q9SWXK2/player-placeholder.png";
    };

  console.log(liveData);

  return (
    <div className="py-4 rounded-lg brd bg-white flex flex-col gap-3">
        <div className="text-base font-medium text-center px-4">Live Ticker</div>
        <div className="brd05"></div>
        <div className="overflow-y-auto flex flex-col gap-3" style={{maxHeight: "40rem"}}>
            {liveData?.events?.map((event, index) => (
                <div className="flex flex-col gap-3">
                    <div className="px-4" key={index}>
                    {!event?.includeInHighLight ? (
                        <div className="flex flex-col gap-3">
                            <div className="text-sm font-bold">{event?.time?.main} {event?.time?.added}</div>
                            <div className="text-sm">{event?.text}</div>
                        </div>
                    ) : (
                        event?.title?.key === "goal" ? (
                            <div className="flex flex-col gap-3">
                                <div className="text-sm font-bold text-green-500">{event?.time?.main} {event?.time?.added}</div>
                                <div className="text-sm">{event?.text}</div>
                                <div className="w-full rounded-lg bg-white brd05 flex justify-between p-2">
                                    <div className="flex gap-3">
                                        <div className="h-8 w-8 rounded-full flex items-center justify-center bbg-white brd05 overflow-hidden">
                                            <img className="h-6 w-6 object-bottom object-contain" alt="playerPhoto" onError={handleError} src={`https://images.fotmob.com/image_resources/playerimages/${event?.players?.[0]?.id}.png`} />
                                        </div>
                                    </div>
                                </div>
                            </div>
                        ) :
                        event?.title?.key === "goal" && (
                            <div className="flex flex-col gap-3">
                                <div className="text-sm font-bold text-green-500">{event?.time?.main} {event?.time?.added}</div>
                                <div className="text-sm">{event?.text}</div>
                                {/* <div className="w-full rounded-lg bg-white brd05 flex justify-between">

                                </div> */}
                            </div>
                        )
                    )}
                    </div>
                    <div className="brd05"></div>
                </div>
            ))}
        </div>
    </div>
  );
}

export default MatchLive;
